// Pseudo code for login method\npublic void UserLogin(string userId)\n{\n    // Generate a new session ID\n    string newSessionId = Guid.NewGuid().ToString();\n\n    // Deactivate any existing sessions for the user\n    DeactivatePreviousSessions(userId);\n\n    // Store new session in the database\n    StoreNewSession(userId, newSessionId);\n\n    // Set the session ID for the current user\n    Session["SessionID"] = newSessionId;\n}\n\nprivate void DeactivatePreviousSessions(string userId)\n{\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        string query = "UPDATE UserSessions SET IsActive = 0 WHERE UserID = @UserID AND IsActive = 1";\n        SqlCommand cmd = new SqlCommand(query, conn);\n        cmd.Parameters.AddWithValue("@UserID", userId);\n        conn.Open();\n        cmd.ExecuteNonQuery();\n    }\n}\n\nprivate void StoreNewSession(string userId, string sessionId)\n{\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        string query = "INSERT INTO UserSessions (UserID, SessionID, IsActive, LastUpdated) VALUES (@UserID, @SessionID, 1, GETDATE())";\n        SqlCommand cmd = new SqlCommand(query, conn);\n        cmd.Parameters.AddWithValue("@UserID", userId);\n        cmd.Parameters.AddWithValue("@SessionID", sessionId);\n        conn.Open();\n        cmd.ExecuteNonQuery();\n    }\n}