using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Create a new ManagementObjectSearcher to query WMI\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher("select * from Win32_Processor");\n\n            // Get the CPU usage percentage from each processor\n            foreach (ManagementObject obj in searcher.Get())\n            {\n                Console.WriteLine("CPU Usage: {0}%", obj["LoadPercentage"]);\n            }\n        }\n        catch (ManagementException e)\n        {\n            Console.WriteLine("An error occurred while querying WMI: " + e.Message);\n        }\n    }\n}