var keyword = cwForm.search1Form;\n\nvar query = from p in context.PROJECTS\n            where (from n in context.PROJECT_NOTES\n                   where n.NOTES.Contains(keyword)\n                   select n.PROJECT_ID).Contains(p.PROJECT_ID)\n            select p;\n\n// If you need additional filtering or ordering, apply it here\nquery = query.Where(p => p.SubmittedDate >= new DateTime(2000, 9, 2) &&\n                         p.SubmittedDate < new DateTime(2009, 3, 2) &&\n                         p.PROVIDER_ID == 1000 &&\n                         p.CATEGORY_ID == null)\n             .OrderBy(p => p.UploadedDate);\n\n// Execute the query and get the results\nvar result = query.ToList();