using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize the dictionary\n        Dictionary<double, char> angleCharacterMap = new Dictionary<double, char>();\n\n        // Function to add or update the mapping\n        void AddOrUpdateMapping(double angle, char character)\n        {\n            if (!angleCharacterMap.ContainsKey(angle))\n            {\n                angleCharacterMap[angle] = character;\n                Console.WriteLine($"Added: {angle} -> {character}");\n            }\n            else\n            {\n                Console.WriteLine($"Angle {angle} already exists with character {angleCharacterMap[angle]}");\n            }\n        }\n\n        // Example usage\n        AddOrUpdateMapping(0.0, 'a');\n        AddOrUpdateMapping(45.0, 'b');\n        AddOrUpdateMapping(0.0, 'c'); // This should indicate the angle already exists\n\n        // Check existing angle\n        double searchAngle = 45.0;\n        if (angleCharacterMap.ContainsKey(searchAngle))\n        {\n            Console.WriteLine($"Angle {searchAngle} exists with character {angleCharacterMap[searchAngle]}");\n        }\n        else\n        {\n            Console.WriteLine($"Angle {searchAngle} does not exist");\n        }\n    }\n}