private void LoadPersonData(int personID)\n{\n    _personInfo = ConnectBLL.BLL.Person.GetPerson(personID);\n\n    // Assuming you have controls to bind data to, like TextBoxes for example\n    if (_personInfo.Tables.Count > 0 && _personInfo.Tables[0].Rows.Count > 0)\n    {\n        DataRow personRow = _personInfo.Tables[0].Rows[0];\n        txtName.Text = personRow["Name"].ToString();\n        txtEmail.Text = personRow["Email"].ToString();\n        // Bind other fields similarly...\n    }\n}