using System.Collections.Generic;\nusing System.Web.Mvc;\n\npublic class TermsController : Controller\n{\n    // This action returns a list of terms in JSON format\n    public JsonResult GetTerms()\n    {\n        var terms = new List<Term>\n        {\n            new Term { Id = 1, Key = "ButtonTitle", Value = "Save" },\n            new Term { Id = 2, Key = "LabelName", Value = "Name" }\n        };\n\n        return Json(terms, JsonRequestBehavior.AllowGet);\n    }\n\n    // This action returns a single term by id in JSON format\n    public JsonResult GetTerm(int id)\n    {\n        var term = new Term { Id = id, Key = "LabelName", Value = "Name" };\n        return Json(term, JsonRequestBehavior.AllowGet);\n    }\n\n    // This action updates a term (for simplicity, it returns the updated term)\n    [HttpPost]\n    public JsonResult UpdateTerm(Term updatedTerm)\n    {\n        // Update logic here\n        // For demonstration, just return the updated term\n        return Json(updatedTerm);\n    }\n}