using System;\nusing System.Net;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string siteUrl = "https://yoursharepointsite.com/sites/yoursite";\n        string listName = "Your List Name";\n        string listItemID = "1"; // ID of the list item you want to check\n        string userLoginName = "domain\\username"; // The user's login name\n\n        // Web service URL\n        string permissionsServiceUrl = siteUrl + "/_vti_bin/Permissions.asmx";\n\n        // Create the web service proxy\n        PermissionsWebService.Permissions permissionsService = new PermissionsWebService.Permissions();\n        permissionsService.Url = permissionsServiceUrl;\n        permissionsService.Credentials = new NetworkCredential("adminusername", "adminpassword", "domain");\n\n        // Get the permissions\n        XmlNode permissionsNode = permissionsService.GetUserEffectivePermissions(listName, listItemID, userLoginName);\n\n        // Display the permissions\n        Console.WriteLine("Effective Permissions for user {0} on item {1}:", userLoginName, listItemID);\n        Console.WriteLine(permissionsNode.OuterXml);\n    }\n}