using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.OleDb;\nusing System.IO;\n\npublic partial class ExportToAccess : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // SQL Database connection string\n        string sqlConnectionString = "Data Source=your_sql_server;Initial Catalog=your_database;User ID=your_user;Password=your_password";\n        \n        // Access database file path\n        string accessFilePath = Server.MapPath("~/App_Data/your_access_db.accdb");\n        \n        // Access Database connection string\n        string accessConnectionString = $"Provider=Microsoft.ACE.OLEDB.12.0;Data Source={accessFilePath};Persist Security Info=False;";\n        \n        // Query to select data from SQL database\n        string sqlQuery = "SELECT * FROM your_table";\n        \n        // Create a DataTable to store the data\n        DataTable dataTable = new DataTable();\n        \n        // Read data from SQL database\n        using (SqlConnection sqlConnection = new SqlConnection(sqlConnectionString))\n        {\n            using (SqlCommand sqlCommand = new SqlCommand(sqlQuery, sqlConnection))\n            {\n                sqlConnection.Open();\n                using (SqlDataReader sqlDataReader = sqlCommand.ExecuteReader())\n                {\n                    dataTable.Load(sqlDataReader);\n                }\n            }\n        }\n        \n        // Export data to Access database\n        using (OleDbConnection accessConnection = new OleDbConnection(accessConnectionString))\n        {\n            accessConnection.Open();\n            foreach (DataRow row in dataTable.Rows)\n            {\n                string insertQuery = "INSERT INTO your_table (Column1, Column2, ...) VALUES (@Value1, @Value2, ...)";\n                using (OleDbCommand accessCommand = new OleDbCommand(insertQuery, accessConnection))\n                {\n                    accessCommand.Parameters.AddWithValue("@Value1", row["Column1"]);\n                    accessCommand.Parameters.AddWithValue("@Value2", row["Column2"]);\n                    // Add other parameters as needed\n                    accessCommand.ExecuteNonQuery();\n                }\n            }\n        }\n    }\n}