using (var db = new YourDataContext())\n{\n    // Create a new instance of your table's entity\n    var newRow = new YourTableEntity\n    {\n        // Set other properties of your entity\n        Column1 = "value1",\n        Column2 = "value2"\n        // Do not set the IDENTITY column, it will be generated\n    };\n\n    // Insert the new row\n    db.YourTable.InsertOnSubmit(newRow);\n\n    // Commit the changes to the database\n    db.SubmitChanges();\n\n    // Now newRow's IDENTITY column (e.g., ID) will have the generated value\n    int generatedId = newRow.ID;\n\n    // You can now use the generatedId as needed\n    Console.WriteLine("Generated ID: " + generatedId);\n}