using System;\nusing System.Collections.Specialized;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing System.Web;\n\npublic void ProxyRequest(string url)\n{\n    // Get the form data from the current request\n    NameValueCollection formData = HttpContext.Current.Request.Form;\n\n    // Create the request\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n    request.Method = "POST";\n    request.ContentType = "application/x-www-form-urlencoded";\n\n    // Build the form data string, respecting duplicate keys\n    StringBuilder postData = new StringBuilder();\n    foreach (string key in formData.AllKeys)\n    {\n        string[] values = formData.GetValues(key);\n        if (values != null)\n        {\n            foreach (string value in values)\n            {\n                if (postData.Length > 0)\n                {\n                    postData.Append("&");\n                }\n                postData.AppendFormat("{0}={1}", HttpUtility.UrlEncode(key), HttpUtility.UrlEncode(value));\n            }\n        }\n    }\n\n    // Write the form data to the request stream\n    byte[] byteArray = Encoding.UTF8.GetBytes(postData.ToString());\n    request.ContentLength = byteArray.Length;\n    using (Stream dataStream = request.GetRequestStream())\n    {\n        dataStream.Write(byteArray, 0, byteArray.Length);\n    }\n\n    // Get the response\n    using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n    {\n        using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n        {\n            string responseFromServer = reader.ReadToEnd();\n            // Process the response as needed\n        }\n    }\n}