using System;\nusing System.Linq; // For using LINQ methods like Select, ToArray\n\nclass Program\n{\n    static void Main()\n    {\n        Console.WriteLine("Enter multiple values separated by spaces:");\n\n        // Read the entire line of input\n        string input = Console.ReadLine();\n\n        // Split the input into parts\n        string[] parts = input.Split(' ');\n\n        // Convert parts to integers (or any other type)\n        int[] numbers = parts.Select(int.Parse).ToArray();\n\n        // Output the values\n        Console.WriteLine("You entered the following numbers:");\n        foreach (var number in numbers)\n        {\n            Console.WriteLine(number);\n        }\n    }\n}