using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n\n        // Load your data from the TXT file into a DataTable\n        DataTable dataTable = LoadDataFromTxtFile("<path_to_your_txt_file>");\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n            using (OracleBulkCopy bulkCopy = new OracleBulkCopy(connection))\n            {\n                bulkCopy.DestinationTableName = "<your_table_name>";\n\n                try\n                {\n                    // Write from the source to the destination.\n                    bulkCopy.WriteToServer(dataTable);\n                    Console.WriteLine("Data loaded successfully.");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine(ex.Message);\n                }\n            }\n        }\n    }\n\n    static DataTable LoadDataFromTxtFile(string filePath)\n    {\n        DataTable table = new DataTable();\n        // Implement your logic to read data from TXT file and fill the DataTable\n        // Ensure that the DataTable schema matches the destination table schema in the Oracle database\n        \n        // Example schema\n        table.Columns.Add("Column1", typeof(string));\n        table.Columns.Add("Column2", typeof(int));\n        // Fill the table with your data\n\n        return table;\n    }\n}