using System;\nusing System.Collections.Generic;\n\npublic class DictionaryWrapper\n{\n    private static IDictionary<string, object> m_Dictionary = new Dictionary<string, object>();\n\n    public object this[string key]\n    {\n        get\n        {\n            if (m_Dictionary.ContainsKey(key))\n            {\n                return m_Dictionary[key];\n            }\n            else\n            {\n                return string.Empty;\n            }\n        }\n        set\n        {\n            if (m_Dictionary.ContainsKey(key))\n            {\n                m_Dictionary[key] = value;\n            }\n            else\n            {\n                m_Dictionary.Add(key, value);\n            }\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        DictionaryWrapper dictionaryWrapper = new DictionaryWrapper();\n        \n        // Setting a value\n        dictionaryWrapper["exampleKey"] = "exampleValue";\n        \n        // Getting a value\n        Console.WriteLine(dictionaryWrapper["exampleKey"]); // Output: exampleValue\n        \n        // Getting a non-existing key\n        Console.WriteLine(dictionaryWrapper["nonExistingKey"]); // Output: \n    }\n}