using System;\nusing System.Management;\n\nclass ScannerIdentifier\n{\n    public static void Main()\n    {\n        // Query for imaging devices\n        ManagementObjectSearcher search = new ManagementObjectSearcher("SELECT * FROM Win32_PnPEntity WHERE PNPClass = 'Imaging Device'");\n\n        ManagementObjectCollection deviceCollection = search.Get();\n\n        foreach (ManagementObject device in deviceCollection)\n        {\n            string deviceName = Convert.ToString(device["Caption"]);\n            \n            // Check if the device is a scanner\n            if (IsScanner(deviceName))\n            {\n                // Add the scanner to the list\n                Console.WriteLine("Scanner found: " + deviceName);\n            }\n        }\n    }\n\n    private static bool IsScanner(string deviceName)\n    {\n        // This is a simple check. You can expand this with more sophisticated checks if needed.\n        return deviceName.ToLower().Contains("scanner");\n    }\n}