using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string remoteMachine = "remote_machine_name_or_ip";\n        string username = "your_username";\n        string password = "your_password";\n\n        ConnectionOptions options = new ConnectionOptions();\n        options.Username = username;\n        options.Password = password;\n        options.Impersonation = ImpersonationLevel.Impersonate;\n\n        ManagementScope scope = new ManagementScope($"\\\\{remoteMachine}\\root\\cimv2", options);\n\n        try\n        {\n            scope.Connect();\n\n            ObjectQuery query = new ObjectQuery("SELECT * FROM Win32_Processor");\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n\n            foreach (ManagementObject obj in searcher.Get())\n            {\n                Console.WriteLine("CPU Usage: {0}%", obj["LoadPercentage"]);\n            }\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("An error occurred while querying WMI: " + e.Message);\n        }\n    }\n}