if (criteria.TaxCreditApprovalYear != null && criteria.TaxCreditApprovalYear.Count > 0)\n{\n    SqlQuery orQuery = new Select().From(Property_Overview.Schema);\n    \n    criteria.TaxCreditApprovalYear.ForEach(year => \n    {\n        SqlQuery tempQuery = new Select().From(Property_Overview.Schema)\n                             .Where(Property_Overview.Columns.EffectiveDate)\n                             .IsBetweenAnd(new DateTime(year, 01, 01), new DateTime(year, 12, 31));\n                             \n        orQuery = orQuery.Or(tempQuery);\n    });\n    \n    qry.And(orQuery);\n}