using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlContent = @"<root>\n                                <child>\n                                    <anotherChild>\n                                        <key>1</key>\n                                    </anotherChild>\n                                    <leaf1>X</leaf1>\n                                    <leaf2>Y</leaf2>\n                                    <leaf3></leaf3>\n                                </child>\n                                <child>\n                                    <anotherChild>\n                                        <key>2</key>\n                                    </anotherChild>\n                                    <leaf1>A</leaf1>\n                                    <leaf2>B</leaf2>\n                                    <leaf3></leaf3>\n                                </child>\n                              </root>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xmlContent);\n\n        string xpath = "//child[anotherChild/key='2']/leaf2";\n        XmlNode node = doc.SelectSingleNode(xpath);\n\n        if (node != null)\n        {\n            Console.WriteLine("Value of leaf2: " + node.InnerText);\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n    }\n}