using System;\nusing System.IO;\n\npublic class DriveInfoExample\n{\n    public static void Main(string[] args)\n    {\n        string path = @"\\fors343a.ww123.somedomain.net\folder\1";\n\n        if (IsUncPath(path))\n        {\n            Console.WriteLine("The path is a UNC path.");\n            // Additional logic to handle UNC path\n        }\n        else\n        {\n            DriveInfo driveInfo = new DriveInfo(Path.GetPathRoot(path));\n            Console.WriteLine($"Drive Name: {driveInfo.Name}");\n            Console.WriteLine($"Drive Type: {driveInfo.DriveType}");\n            Console.WriteLine($"Volume Label: {driveInfo.VolumeLabel}");\n            Console.WriteLine($"Drive Format: {driveInfo.DriveFormat}");\n            Console.WriteLine($"Available Free Space: {driveInfo.AvailableFreeSpace}");\n        }\n    }\n\n    private static bool IsUncPath(string path)\n    {\n        return Uri.TryCreate(path, UriKind.Absolute, out Uri uriResult) && uriResult.IsUnc;\n    }\n}