using System;\n\n[Serializable]\npublic class SerializableExample { }\n\npublic class MyClass<T>\n{\n    public MyClass()\n    {\n        if (!typeof(T).IsSerializable)\n        {\n            throw new InvalidOperationException($"Type {typeof(T).Name} must be serializable.");\n        }\n    }\n\n    // Other class members here\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var instance = new MyClass<SerializableExample>(); // This works\n\n        // This will throw an exception because NonSerializableExample is not marked as Serializable\n        // var instance2 = new MyClass<NonSerializableExample>();\n    }\n}\n\npublic class NonSerializableExample { }