using System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void GeneratePdfLabels()\n{\n    Document document = new Document(PageSize.A4, 50, 50, 25, 25);\n    PdfWriter writer = PdfWriter.GetInstance(document, new FileStream("Labels.pdf", FileMode.Create));\n    document.Open();\n\n    PdfPTable table = new PdfPTable(2); // Assuming 2 columns of labels\n    table.WidthPercentage = 100;\n\n    for (int i = 0; i < 10; i++) // Assuming 10 rows of labels\n    {\n        PdfPCell cell = new PdfPCell();\n        cell.Border = Rectangle.NO_BORDER;\n\n        // Add text to the label\n        cell.AddElement(new Paragraph("Label " + (i + 1)));\n\n        // Add barcode to the label\n        Barcode128 barcode = new Barcode128();\n        barcode.Code = "1234567890";\n        Image barcodeImage = barcode.CreateImageWithBarcode(writer.DirectContent, null, null);\n        cell.AddElement(barcodeImage);\n\n        table.AddCell(cell);\n    }\n\n    document.Add(table);\n    document.Close();\n}