using Raven.Client.Documents;\n\n   var store = new DocumentStore\n   {\n       Urls = new[] { "http://your-ravendb-server" },\n       Database = "your-database-name"\n   };\n   store.Initialize();\n\n   using (var session = store.OpenSession())\n   {\n       // Store a document\n       session.Store(new YourDocument { Id = "your-id", Data = yourData });\n       session.SaveChanges();\n   }