using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\n<head>\n<title>title</title>\n</head>\n<body>\n    <a href=\"aaa.asp?id=1\"> I want to get this text </a>\n    <div>\n        <h1>this is my want!!</h1>\n        <b>this is my want!!!</b>\n    </div>\n</body>\n</html>";\n\n        // Use Regex to remove HTML tags\n        string output = Regex.Replace(input, "<.*?>", String.Empty);\n\n        // Optionally, you can also decode HTML entities if needed\n        output = System.Net.WebUtility.HtmlDecode(output);\n\n        Console.WriteLine(output);\n    }\n}