using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string recycleBinPath = Path.Combine(Environment.GetEnvironmentVariable("SystemDrive") + @"\$Recycle.Bin");\n\n        if (Directory.Exists(recycleBinPath))\n        {\n            Console.WriteLine("Recycle Bin directory: " + recycleBinPath);\n        }\n        else\n        {\n            Console.WriteLine("Recycle Bin directory not found.");\n        }\n    }\n}