using System;\nusing System.IO;\nusing PdfiumViewer;\nusing ImageMagick;\n\nclass Program\n{\n    static void Main()\n    {\n        string pdfPath = "input.pdf";\n        string tiffPath = "output.tiff";\n\n        using (var document = PdfDocument.Load(pdfPath))\n        {\n            using (var stream = new MemoryStream())\n            {\n                int dpi = 300; // Set the desired DPI\n                var settings = new PdfRendererSettings { Dpi = dpi };\n\n                var renderer = new PdfRenderer();\n                renderer.Render(document, stream, settings);\n\n                stream.Position = 0;\n\n                using (var image = new MagickImageCollection(stream))\n                {\n                    image.Write(tiffPath, MagickFormat.Tiff);\n                }\n            }\n        }\n    }\n}