using System;\nusing System.Data.SqlClient;\nusing System.Text;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string query = "select * from mytable where clientID = 431620 for xml auto";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand(query, connection);\n            connection.Open();\n\n            StringBuilder sb = new StringBuilder();\n            using (SqlDataReader dr = cmd.ExecuteReader())\n            {\n                while (dr.Read())\n                {\n                    // Assuming the XML is in the first column\n                    string xml = (string)dr[0];\n                    // Escape invalid characters\n                    string escapedXml = EscapeInvalidXmlChars(xml);\n                    sb.Append(escapedXml);\n                }\n            }\n\n            // Parse the XML from the StringBuilder\n            using (XmlReader xrd = XmlReader.Create(new System.IO.StringReader(sb.ToString())))\n            {\n                while (xrd.Read())\n                {\n                    string s = xrd.ReadOuterXml();\n                    Console.WriteLine(s);  // Or add to your records collection\n                }\n            }\n        }\n    }\n\n    static string EscapeInvalidXmlChars(string text)\n    {\n        if (string.IsNullOrEmpty(text))\n            return text;\n\n        StringBuilder escapedText = new StringBuilder();\n        foreach (char c in text)\n        {\n            if (XmlConvert.IsXmlChar(c))\n            {\n                escapedText.Append(c);\n            }\n            else\n            {\n                escapedText.AppendFormat("&#x{0:X};", (int)c);\n            }\n        }\n        return escapedText.ToString();\n    }\n}