public class RepositoryBase<T> where T : class\n{\n    protected YourDbContext db;\n\n    public RepositoryBase(YourDbContext context)\n    {\n        db = context;\n    }\n\n    public IQueryable<T> SelectAll()\n    {\n        return db.Set<T>().AsQueryable();\n    }\n\n    public IQueryable<U> SelectView<U>() where U : class\n    {\n        return db.Set<U>().AsQueryable();\n    }\n}