using System;\nusing System.IO;\nusing System.IO.Pipes;\n\nclass PipeClient\n{\n    static void Main()\n    {\n        using (NamedPipeClientStream pipeClient = new NamedPipeClientStream(".", "testpipe", PipeDirection.Out))\n        {\n            pipeClient.Connect();\n            using (StreamWriter writer = new StreamWriter(pipeClient))\n            {\n                writer.AutoFlush = true;\n                int numberToSend = 42; // Example integer to send\n                writer.WriteLine(numberToSend);\n                Console.WriteLine("Sent message: " + numberToSend);\n            }\n        }\n    }\n}