public class TimeoutManager\n{\n    private static readonly TimeSpan timeoutDuration = TimeSpan.FromMinutes(5);\n    private static Dictionary<string, DateTime> userLastActive = new Dictionary<string, DateTime>();\n\n    public static void UpdateUserActivity(string userId)\n    {\n        userLastActive[userId] = DateTime.Now;\n    }\n\n    public static void CheckForTimeouts()\n    {\n        var now = DateTime.Now;\n        foreach (var user in userLastActive.Keys.ToList())\n        {\n            if (now - userLastActive[user] > timeoutDuration)\n            {\n                UserQueue.DequeueUser(user);\n                userLastActive.Remove(user);\n            }\n        }\n    }\n}