public void SaveItem(Item item)\n{\n    string commandText = "dbo.Save";\n\n    using (SqlConnection sql = new SqlConnection(_connString.ConnectionString))\n    {\n        using (SqlCommand cmd = sql.CreateCommand())\n        {\n            cmd.CommandText = commandText;\n            cmd.CommandType = CommandType.StoredProcedure;\n\n            AddParameter(cmd, "identity", item.Identity);\n            AddParameter(cmd, "name", item.Name);\n            AddParameter(cmd, "desc", item.Description);\n            AddParameter(cmd, "title", item.Title);\n            // Add more parameters as needed\n\n            sql.Open();\n            cmd.ExecuteNonQuery();\n\n            // Handle out parameters if needed\n        }\n    }\n}\n\nprivate void AddParameter(SqlCommand cmd, string paramName, object value)\n{\n    SqlParameter param = new SqlParameter(paramName, value ?? DBNull.Value);\n    param.Direction = ParameterDirection.Input;\n    cmd.Parameters.Add(param);\n}