using System;\nusing System.Windows.Forms;\n\npublic class BrowserManager\n{\n    private WebBrowser webBrowser;\n    private string[] pages;\n    private int currentPageIndex;\n\n    public BrowserManager(WebBrowser webBrowser)\n    {\n        this.webBrowser = webBrowser;\n        this.pages = new string[10]; // Assuming we want to manage 10 pages\n        this.currentPageIndex = -1;\n    }\n\n    public void SaveCurrentPage()\n    {\n        if (webBrowser.Document != null)\n        {\n            pages[currentPageIndex] = webBrowser.DocumentText;\n        }\n    }\n\n    public void LoadPage(int pageIndex)\n    {\n        if (pageIndex >= 0 && pageIndex < pages.Length && pages[pageIndex] != null)\n        {\n            currentPageIndex = pageIndex;\n            webBrowser.DocumentText = pages[pageIndex];\n        }\n    }\n\n    public void SetCurrentPageIndex(int pageIndex)\n    {\n        if (pageIndex >= 0 && pageIndex < pages.Length)\n        {\n            currentPageIndex = pageIndex;\n        }\n    }\n\n    public int GetCurrentPageIndex()\n    {\n        return currentPageIndex;\n    }\n}\n\n// Usage\npublic static void Main()\n{\n    Application.EnableVisualStyles();\n    Application.SetCompatibleTextRenderingDefault(false);\n\n    WebBrowser webBrowser = new WebBrowser();\n    BrowserManager browserManager = new BrowserManager(webBrowser);\n\n    // Assuming you have already navigated to a page\n    browserManager.SetCurrentPageIndex(0);\n    browserManager.SaveCurrentPage();\n\n    // Switch to another page\n    browserManager.SetCurrentPageIndex(1);\n    browserManager.LoadPage(0);\n\n    Application.Run(new Form { Controls = { webBrowser } });\n}