using System;\nusing System.Collections.Generic;\nusing System.Net;\nusing System.Web.Script.Serialization;\n\npublic class InnerResponseData\n{\n    public string translatedText { get; set; }\n}\n\npublic class ResponseData\n{\n    public List<OuterResponseData> responseData { get; set; }\n    public object responseDetails { get; set; }\n    public int responseStatus { get; set; }\n}\n\npublic class OuterResponseData\n{\n    public InnerResponseData responseData { get; set; }\n    public object responseDetails { get; set; }\n    public int responseStatus { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string responseJson = "{\n  \"responseData\": [{\n    \"responseData\": {\n      \"translatedText\": \"elefante\"\n    },\n    \"responseDetails\": null,\n    \"responseStatus\": 200\n  }, {\n    \"responseData\": {\n      \"translatedText\": \"Burro\"\n    },\n    \"responseDetails\": null,\n    \"responseStatus\": 200\n  }],\n  \"responseDetails\": null,\n  \"responseStatus\": 200\n}";\n\n        JavaScriptSerializer serializer = new JavaScriptSerializer();\n        ResponseData translation = serializer.Deserialize<ResponseData>(responseJson);\n\n        foreach (var data in translation.responseData)\n        {\n            Console.WriteLine(data.responseData.translatedText);\n        }\n    }\n}