using System;\nusing System.Net;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a WebProxy object.\n        WebProxy proxy = new WebProxy("http://your-proxy-address:proxy-port", true);\n        \n        // If the proxy requires authentication, use NetworkCredential.\n        proxy.Credentials = new NetworkCredential("proxyUsername", "proxyPassword");\n\n        // Assign the proxy to the WebRequest.\n        WebRequest request = WebRequest.Create("http://your-web-service-url");\n        request.Proxy = proxy;\n\n        // If you are using a WebClient, you can set the proxy like this:\n        WebClient client = new WebClient();\n        client.Proxy = proxy;\n\n        // Send the request and get the response.\n        try\n        {\n            WebResponse response = request.GetResponse();\n            // Process the response here.\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}