using (var context = new YourDataContext("your_connection_string"))\n{\n    var query = from item in context.XmlTableTests\n                let messageType = (int?)item.Content.Element("root").Element("item").Element("MessageType")\n                let value = (float?)context.GetValueFromXml(item.Content, "(/root/item/value)[1]")\n                where messageType == 1\n                select new\n                {\n                    item,\n                    Value = value\n                };\n\n    var result = query.Take(10).ToList();\n}