using System;\n    using System.Data;\n    using System.Data.SqlClient;\n\n    public class MovieSearch\n    {\n        private string connectionString = "your_connection_string_here";\n\n        public DataTable SearchMovies(string term1, string term2)\n        {\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                using (SqlCommand command = new SqlCommand("SearchMovies", connection))\n                {\n                    command.CommandType = CommandType.StoredProcedure;\n                    command.Parameters.Add(new SqlParameter("@SearchTerm1", term1));\n                    command.Parameters.Add(new SqlParameter("@SearchTerm2", term2));\n\n                    SqlDataAdapter adapter = new SqlDataAdapter(command);\n                    DataTable resultTable = new DataTable();\n                    adapter.Fill(resultTable);\n\n                    return resultTable;\n                }\n            }\n        }\n    }