// Assuming you have a DateTime object representing local time\nDateTime localTime = DateTime.Now;\n\n// Convert local time to UTC\nDateTime utcTime = localTime.ToUniversalTime();\n\n// Insert the UTC time into the database\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    string query = "INSERT INTO YourTable (YourDateTimeColumn) VALUES (@UtcTime)";\n    using (SqlCommand command = new SqlCommand(query, connection))\n    {\n        command.Parameters.AddWithValue("@UtcTime", utcTime);\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}