using System.Windows;\nusing System.Windows.Controls;\n\npublic partial class SelectorControl : UserControl\n{\n    public SelectorControl()\n    {\n        InitializeComponent();\n        this.DataContext = new SelectorViewModel<ISelectableItem>();\n    }\n\n    private void OnItemDoubleClick(object sender, System.Windows.Input.MouseButtonEventArgs e)\n    {\n        ListBox listBox = sender as ListBox;\n        if (listBox != null && listBox.SelectedItem != null)\n        {\n            var viewModel = (SelectorViewModel<ISelectableItem>)this.DataContext;\n            viewModel.MoveItem((ISelectableItem)listBox.SelectedItem);\n        }\n    }\n}