public void BindListToTextBox(TextBox textBox, List<string> list)\n{\n    textBox.Text = string.Join(Environment.NewLine, list);\n}\n\n// Usage example:\npublic void ExampleUsage()\n{\n    string writeDir = "path/to/your/xml/file.xml";\n    List<string> agentDetails = GetAgentsDetails(writeDir);\n\n    TextBox textBox = new TextBox(); // Assume you have a TextBox instance\n    BindListToTextBox(textBox, agentDetails);\n}