using System;\nusing System.Runtime.Caching;\nusing System.Xml.Linq;\n\npublic class TextTemplateManager\n{\n    private readonly ObjectCache _cache = MemoryCache.Default;\n\n    public string GetTemplateText(string key, string xmlFilePath)\n    {\n        string cacheKey = "Template_" + key;\n        string templateText = _cache[cacheKey] as string;\n\n        if (templateText == null)\n        {\n            // Load the XML file and fetch the text\n            XElement xml = XElement.Load(xmlFilePath);\n            templateText = xml.Element(key)?.Value;\n\n            if (templateText != null)\n            {\n                // Cache the text for future use\n                CacheItemPolicy policy = new CacheItemPolicy\n                {\n                    AbsoluteExpiration = DateTimeOffset.Now.AddMinutes(10) // Adjust as needed\n                };\n                _cache.Set(cacheKey, templateText, policy);\n            }\n        }\n\n        return templateText;\n    }\n}