using (var context = new YourDbContext())\n{\n    // Assuming you have a Fruit object from a previous query\n    var fruit = context.Fruits.FirstOrDefault(f => f.FruitID == someFruitID);\n\n    if (fruit != null)\n    {\n        fruit.FruitName = "NewFruitName";\n        fruit.FruitStatusID = newStatusID; // newStatusID should be the ID of the new status\n\n        context.SaveChanges();\n    }\n}