using iTextSharp.text;\nusing iTextSharp.text.pdf;\nusing System.IO;\n\npublic void MakePdfReadOnly(string inputPdfPath, string outputPdfPath)\n{\n    using (Stream inputPdfStream = new FileStream(inputPdfPath, FileMode.Open, FileAccess.Read, FileShare.Read))\n    using (Stream outputPdfStream = new FileStream(outputPdfPath, FileMode.Create, FileAccess.Write, FileShare.None))\n    {\n        PdfReader reader = new PdfReader(inputPdfStream);\n        PdfStamper stamper = new PdfStamper(reader, outputPdfStream);\n        \n        // Setting permissions to restrict copying, printing, and modifying the document\n        stamper.SetEncryption(\n            null, // No user password\n            null, // No owner password\n            PdfWriter.ALLOW_PRINTING | PdfWriter.ALLOW_COPY, // Permissions\n            PdfWriter.ENCRYPTION_AES_128\n        );\n\n        stamper.Close();\n        reader.Close();\n    }\n}