using System;\n\npublic class Program\n{\n    // Define a delegate with the same signature as your methods\n    public delegate string MessageDelegate(int min, int max);\n\n    public static void Main()\n    {\n        // Create instances of the delegate, pointing to different methods\n        MessageDelegate weightDelegate = new MessageDelegate(WeightInvalid);\n        MessageDelegate heightDelegate = new MessageDelegate(HeightInvalid);\n\n        // Invoke the delegates\n        Console.WriteLine(weightDelegate(50, 100));  // Output: Weight must be between 50 and 100 kg.\n        Console.WriteLine(heightDelegate(150, 200)); // Output: Height must be between 150 and 200 cm.\n    }\n\n    // Example methods matching the delegate signature\n    public static string WeightInvalid(int min, int max)\n    {\n        return $"Weight must be between {min} and {max} kg.";\n    }\n\n    public static string HeightInvalid(int min, int max)\n    {\n        return $"Height must be between {min} and {max} cm.";\n    }\n}