using Stimulsoft.Report;\nusing Stimulsoft.Report.Web;\n\npublic partial class Reports : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Create a new report\n        StiReport report = new StiReport();\n\n        // Load the report template\n        report.Load(Server.MapPath("~/Reports/YourReport.mrt"));\n\n        // Set the connection string to the RESTful web service\n        report.Dictionary.Databases.Clear();\n        var dataSource = new Stimulsoft.Report.Dictionary.StiJsonDatabase("RESTConnection", "https://yourapi.com/api/data");\n        report.Dictionary.Databases.Add(dataSource);\n\n        // Render the report\n        report.Render();\n\n        // Display the report in the web viewer\n        StiWebViewer1.Report = report;\n    }\n}