using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Person\n{\n    public string Employer { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<Person> personList = new List<Person>\n        {\n            new Person { Employer = "a" },\n            new Person { Employer = "a" },\n            new Person { Employer = "a" },\n            new Person { Employer = "b" },\n            new Person { Employer = "b" },\n            new Person { Employer = "c" },\n            new Person { Employer = "c" }\n        };\n\n        string prevEmployer = string.Empty;\n        foreach (var group in personList.GroupBy(p => p.Employer))\n        {\n            if (group.Key != prevEmployer)\n            {\n                doSomething();\n                prevEmployer = group.Key;\n            }\n            // more code\n        }\n    }\n\n    public static void doSomething()\n    {\n        Console.WriteLine("Employer changed!");\n    }\n}