public static class TraceContextExtensions\n   {\n       public static string WriteToString(this TraceContext traceContext)\n       {\n           StringWriter stringWriter = new StringWriter();\n           foreach (TraceContextRecord traceRecord in traceContext.TraceRecords)\n           {\n               stringWriter.WriteLine($"{traceRecord.Category}: {traceRecord.Message}");\n           }\n           return stringWriter.ToString();\n       }\n   }