using System;\nusing Microsoft.WindowsMobile.PocketOutlook;\n\npublic class SmsExtractor\n{\n    public static void Main()\n    {\n        MessagingApplication.Synchronize();\n\n        SmsMessageCollection smsMessages = new SmsMessageCollection();\n        smsMessages = MessagingApplication.SmsMessages;\n\n        foreach (SmsMessage sms in smsMessages)\n        {\n            Console.WriteLine("From: " + sms.From.Address);\n            Console.WriteLine("Body: " + sms.Body);\n            Console.WriteLine("Received: " + sms.Received.ToString());\n            Console.WriteLine("---------------------");\n        }\n    }\n}