using System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        float[] values = { 0.5f, 2.3f, 1.1f, 4.7f, 3.9f }; // Example array\n        int n = values.Length;\n        int topN = 4; // Number of top values you want\n\n        // Create an array of indices\n        int[] indices = Enumerable.Range(0, n).ToArray();\n\n        // Sort the indices based on the corresponding values in descending order\n        Array.Sort(indices, (i1, i2) => values[i2].CompareTo(values[i1]));\n\n        // Print the top N values and their original positions\n        for (int i = 0; i < topN; i++)\n        {\n            Console.WriteLine($"Value: {values[indices[i]]}, Position: {indices[i]}");\n        }\n    }\n}