protected void Page_Load(object sender, EventArgs e)\n{\n    // Your binary blob\n    byte[] binaryBlob = GetBinaryBlob(); // Implement this method to get your binary data\n    string fileName = "example.bin"; // Your file name\n\n    // Clear the response and set the content type and headers\n    Response.Clear();\n    Response.ContentType = "application/octet-stream";\n    Response.AddHeader("Content-Disposition", $"attachment; filename={fileName}");\n    Response.AddHeader("Content-Length", binaryBlob.Length.ToString());\n\n    // Write the binary blob to the response output stream\n    Response.OutputStream.Write(binaryBlob, 0, binaryBlob.Length);\n    \n    // End the response\n    Response.Flush();\n    Response.End();\n}\n\n// Example method to get a binary blob\nprivate byte[] GetBinaryBlob()\n{\n    // For example purposes, returning a byte array\n    return new byte[] { 0x01, 0x02, 0x03, 0x04, 0x05 };\n}