protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Register the startup script that will run when the page is fully loaded\n        if (!ClientScript.IsStartupScriptRegistered("blockUIAlertStartup"))\n        {\n            string startupScript = @"\n                window.onload = function() {\n                    var script = document.createElement('script');\n                    script.type = 'text/javascript';\n                    script.text = ""yourFunctionToCall();""; // Replace yourFunctionToCall with your actual function\n                    document.body.appendChild(script);\n                };";\n            ClientScript.RegisterStartupScript(this.GetType(), "blockUIAlertStartup", startupScript, true);\n        }\n    }\n    // Other server-side code\n}