// content.aspx.cs (Code-Behind)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Check if the master page is of the expected type\n        if (this.Master != null && this.Master is SampleMaster)\n        {\n            // Cast the Master property to the specific type\n            SampleMaster master = (SampleMaster)this.Master;\n            // Access the label and change its text\n            master.lblHeading.Text = "New Heading Based on Content";\n        }\n    }\n}