using System;\nusing Microsoft.Office.Interop.Word;\n\nclass Program\n{\n    static void Main()\n    {\n        Application wordApp = new Application();\n        Document doc = null;\n        \n        try\n        {\n            // Make the application visible (optional)\n            wordApp.Visible = true;\n\n            // Open an existing document or create a new one\n            object missing = Type.Missing;\n            object fileName = @"C:\path\to\your\document.docx";\n            doc = wordApp.Documents.Open(ref fileName, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing);\n\n            // Make some changes to the document\n            doc.Content.Text = "Hello, this is a test.";\n\n            // Save the changes\n            doc.Save();\n\n            // Optionally, save the document as a new file\n            // object newFileName = @"C:\path\to\your\newdocument.docx";\n            // doc.SaveAs2(ref newFileName);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine(ex.Message);\n        }\n        finally\n        {\n            // Clean up\n            if (doc != null)\n            {\n                doc.Close(false);\n                doc = null;\n            }\n\n            wordApp.Quit();\n            wordApp = null;\n        }\n    }\n}